import React, { Component } from 'react'
import HotelImages from './../hotelImages/hotelImages'

class Hotel extends Component {
    constructor(props) {
        super(props);
        this.state = {
            id: this.props.match.params.id,
            hotel: this.props.location.state.hotel
        }
    }
    render() {
        // if(this.props.match)
        //     console.log(this.props.match.params.id);
        // console.log('inside room');
        // console.log(this.props);
        console.log(this.props.location.state.hotel);
        return <div className='container-fluid' >
            <div className='row' >
                <div className='col-sm-6' >
                    <HotelImages id={this.state.id} />
                    <div className='row'>
                        <div className='col-6'>
                            <button style={{width: "80%",marginLeft: '10%',fontSize:'24px',fontWeight:'500'}} className='btn btn-primary'>book</button>
                        </div>
                        <div className='col-6'>
                            <button style={{width: "80%",marginLeft: '10%',fontSize:'24px',fontWeight:'500'}} className='btn btn-primary'>add to whishlist</button>
                        </div>
                    </div>
                </div>
                <div className='col-sm-6' >
                    <h3 > {this.state.hotel.name} </h3>
                    <h5 > {this.state.hotel.city + ',' + this.state.hotel.country} </h5>
                </div> </div>
        </div>
    }
}
export default Hotel;
