import React,{Component} from 'react'
import getReviews from '../../api_calls/getReviews';

class HotelReviews extends Component{
    
    constructor(props){
        super(props);
        this.state={
            reviews: null
        }
    }


    componentDidMount(){
        getReviews(this.props.hotel)
        .then((res)=>{
            this.setState({reviews: res.vpm_featured_reviews})
        })
    }

    render(){
        let {reviews} = this.state;
        let review_list = [];
        if(reviews)
        {
            console.log(reviews)
            review_list = reviews.map((review,index)=>{
                return <div className='' key={index}>
                    <div className='row user'>
                        <div className='col-1'>
                            <img src={review.author.avatar} alt='a' />
                        </div>
                        {review.author.name}
                    </div>
                    <div className='review'>
                        <div className='rating'>{review.average_rating_out_of_10}</div>
                        <div className='pros'>{review.pros}</div>
                    </div>
                </div>
            })
        }
        return <div>
            {review_list}
        </div>
    }
}

export default HotelReviews;
