import React, { Component } from 'react'
import { Form, Button } from 'react-bootstrap'
class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isValidPassword: null,
            isInvalidPassword: null,
            isValidEmail: null,
            isInvalidEmail: null
        }
        this.onSubmit = this.onSubmit.bind(this);
        this.validateEmail = this.validateEmail.bind(this);
    }
    validateEmail(event) {
        let mail = event.target.value;
        let mailRegExp = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
        if (mailRegExp.test(mail)) {
            this.setState({ isValidEmail: true, isInvalidEmail: false })
        }
        else
            this.setState({ isValidEmail: false, isInvalidEmail: true })

    }
    validatePassword(event) {
        let password = event.target.value;
        var passRegExp = /^[A-Za-z]\w{7,14}$/;
        if (password.value.match(passRegExp)) {
            this.setState({ isValidEmail: true, isInvalidEmail: false })    
        }
        else
        {
            this.setState({ isValidPassword: false, isInvalidPassword: true })
        }
    }
    onSubmit(event) {
        event.preventDefault();
        console.log(event.target.email.value);
    }
    render() {
        let { isValidEmail, isInvalidEmail, isValidPassword, isInvalidPassword } = this.state;
        return <div className='container-fluid' style={{ backgroundColor: '#f9f9f9' }}>
            <div className='row' style={{ padding: '2%' }}>
                <div style={{ margin: 'auto', fontSize: '30px', fontWeight: '400' }}>Sign in</div>
            </div>
            <Form onSubmit={this.onSubmit} className='col-lg-4' style={{ margin: 'auto', padding: '5%', backgroundColor: 'white', borderRadius: '2%', border: '1px solid #e8e5e5' }}>
                <Form.Group controlId="formBasicEmail" style={{ padding: '1%' }}>
                    <Form.Label>Email address</Form.Label>
                    <Form.Control isValid={isValidEmail} isInvalid={isInvalidEmail} onBlur={this.validateEmail} type="email" placeholder="Enter email" name='email' />
                    {/* <Form.Text className="text-muted">
                        We'll never share your email with anyone else.
                        </Form.Text> */}
                </Form.Group>

                <Form.Group controlId="formBasicPassword" style={{ padding: '1%' }}>
                    <Form.Label>Password</Form.Label>
                    <Form.Control isValid={isValidPassword} isInvalid={isInvalidPassword} type="password" placeholder="Password" name='password' />
                </Form.Group>

                {/* <Form.Group controlId="formBasicCheckbox">
                        <Form.Check type="checkbox" label="Check me out" />
                    </Form.Group> */}
                <div className='row' style={{ padding: '1%' }}>
                    <Button variant="primary" type="submit" style={{ margin: 'auto' }}>
                        Submit
                        </Button>
                </div>
            </Form>
        </div>
    }
}
export default Login;
